CREATE TABLE "public"."post_liked" ("people_id" uuid NOT NULL, "post_id" uuid NOT NULL, "liked_at" timestamptz NOT NULL DEFAULT now(), PRIMARY KEY ("people_id","post_id") , FOREIGN KEY ("people_id") REFERENCES "public"."people"("id") ON UPDATE cascade ON DELETE restrict, FOREIGN KEY ("post_id") REFERENCES "public"."post"("id") ON UPDATE cascade ON DELETE restrict, UNIQUE ("people_id", "post_id"));
